CSbuttons.init = function () {
    CSbuttons.socialSharing();
};

CSbuttons.socialSharing = function () {
    var $buttons = CSbuttons.cache.$shareButtons,
        $shareLinks = $buttons.find('a'),
        permalink = $buttons.attr('data-permalink');
        permalinkGitHub = $buttons.attr('data-permalink-github');

    // Get share stats from respective APIs
    var $fbLink = $('.share-facebook'),
        $twitLink = $('.share-twitter'),
        $pinLink = $('.share-pinterest'),
        $googleLink = $('.share-google');
        $githubLink = $('.share-github');

    if ($fbLink.length) {
        $.getJSON('https://graph.facebook.com/?id=' + permalink + '&callback=?', function (data) {
            if (data.shares) {
                $fbLink.find('.share-count').text(data.shares).addClass('is-loaded');
            } else {
                $fbLink.find('.share-count').remove();
            }
        });
    }

    if ($twitLink.length) {
        $.getJSON('https://cdn.api.twitter.com/1/urls/count.json?url=' + permalink + '&callback=?', function (data) {
            if (data.count > 0) {
                $twitLink.find('.share-count').text(data.count).addClass('is-loaded');
            } else {
                $twitLink.find('.share-count').remove();
            }
        });
    }

    if ($pinLink.length) {
        $.getJSON('https://api.pinterest.com/v1/urls/count.json?url=' + permalink + '&callback=?', function (data) {
            if (data.count > 0) {
                $pinLink.find('.share-count').text(data.count).addClass('is-loaded');
            } else {
                $pinLink.find('.share-count').remove();
            }
        });
    }

    if ($githubLink.length) {
        $.getJSON('https://api.github.com/repos/' + permalinkGitHub + '/stargazers', function (data) {
            if (data.length > 0) {
                $githubLink.find('.share-count').text(data.length).addClass('is-loaded');
            } else {
                $githubLink.find('.share-count').remove();
            }
        });
    }

    if ($googleLink.length) {
        // Can't currently get Google+ count with JS, so just pretend it loaded
        $googleLink.find('.share-count').addClass('is-loaded');
    }

    // Share popups
    $shareLinks.on('click', function (e) {
        var el = $(this),
            popup = el.attr('class').replace('-', '_'),
            link = el.attr('href'),
            w = 700,
            h = 400;

        if (!el.hasClass('share-github')) {
            e.preventDefault();

            // Set popup sizes
            switch (popup) {
            case 'share-twitter':
                h = 300;
                break;
            case 'share-fancy':
                w = 480;
                h = 720;
                break;
            case 'share-google':
                w = 500;
                break;
            }

            window.open(link, popup, 'width=' + w + ', height=' + h);
        }
    });
};

